<?php

  require_once __DIR__ . "/../base/baseBattleField.php";
  require_once "warrior.php";


class BattleField extends BaseBattleField
  {
    static function createMyWarrior()
    {
      $warrior = new PokemonWarrior($GLOBALS['warriorID']);
      $weapon = new Weapon('cannon a eau',90000);
      $weapon->setImageUrl("data:image/jpeg;base64,");
      $warrior->setWeapon($weapon);
      $warrior->setImageUrl("https://www.pokepedia.fr/images/thumb/4/4d/Lokhlass-RFVF.png/623px-Lokhlass-RFVF.png");
      $warrior->saveNew();
    }

    static function createOtherWarrior()
    {
      $warrior2 = new marvelWarrior('J2');
      $weapon = new Weapon('zbub',50000);
      $weapon->setImageUrl("https://media.gettyimages.com/vectors/-vector-id1166207450?s=612x612");
      $warrior2->setWeapon($weapon);
      $warrior2->setImageUrl("https://resize-gulli.jnsmedia.fr/r/890,__ym__/img//var/jeunesse/storage/images/gulli/chaine-tv/dessins-animes/pokemon/pokemon/pikachu/26571681-1-fre-FR/Pikachu.jpg");
      $warrior2->saveNew();
    }
  }